k(ts.target)if tsDistance<675 and tsDistance>400 then if Ready(_Q)then CastSpell(_Q,ts.target)checkQ=os.clock()end;if Ready(_E)and os.clock()>checkW+3 then CastSpell(_E,ts.target)end end;if tsDistance<400 then if Ready(_Q)then CastSpell(_Q,ts.target)checkQ=os.clock()end;if Ready(_W)and os.clock()<qMark+1 or Ready(_Q)==false and os.clock()>checkQ+1 then CastSpell(_W)checkW=os.clock()end end end;if ts.target==nil then player:MoveTo(mousePos.x,mousePos.z)end end;function Farm()EnemyMinions:update()for n,o in pairs(EnemyMinions.objects)do local p=getDmg("Q",o,myHero)local q=getDmg("W",o,myHero)if not o.dead then if Menu.farm.farmQ and GetDistance(o)>400 or not Ready(_W)or not Menu.farm.farmW and GetDistance(o)<675 and p>o.health and Ready(_Q)and GetDistance(o)>195 or Menu.farm.farmAA==false or not SOWi:CanAttack()then CastSpell(_Q,o)end;if Menu.farm.farmW and GetDistance(o)>195 or Menu.farm.farmAA==false or not SOWi:CanAttack()and GetDistance(o)<400 and q>o.health and Ready(_W)then CastSpell(_W)end end end;if Menu.farm.farmAA then local target=SOWKillableMinion()or SOWi:GetTarget()SOWOrbWalk(target)else player:MoveTo(mousePos.x,mousePos.z)end end;function AutoKS()if ts.target~=nil then for n,r in pairs(enemyHeroes)do if not r.dead and GetDistance(r)<700 and qweDMG(r)>r.health then if Ready(_Q)and GetDistance(r)<675 then CastSpell(_Q,r)end;if Ready(_E)then CastSpell(_E,r)end;if Ready(_W)and os.clock()<qMark+1 and GetDistance(r)<375 then CastSpell(_W,r)end end end end end;function MaxSpells()if Menu.ads.spellLVL==1 then autoLevelSetSequence(maxNone)end;if Menu.ads.spellLVL==2 then autoLevelSetSequence(maxQWE)end;if Menu.ads.spellLVL==3 then autoLevelSetSequence(maxWQE)end end;function Initiate()ts:update()MaxSpells()DFG=GetInventorySlotItem(3128)if ts.target~=nil then tsDistance=GetDistance(ts.target)end end;function WardJump()player:MoveTo(mousePos.x,mousePos.z)if ward and GetTickCount()<castAt+1000 and Ready(_E)then CastSpell(_E,ward)end;if GetTickCount()>LastWard+3000 